mapper.select:
SELECT *
FROM Companies


		company_no = #{company_no}


		company_name = #{company_name}


		dealer_phone = #{dealer_phone}


		dealer_name = #{dealer_name}


		phone = #{phone}


		address = #{address}


		ceo_name = #{ceo_name}


		bank_name = #{bank_name}


		account_no = #{account_no}


		note = #{note}


ORDER BY COMPANY_NO

mapper.update:
UPDATE Companies
company_name = #{company_name}, dealer_phone = #{dealer_phone}, dealer_name = #{dealer_name}, phone = #{phone}, address = #{address}, ceo_name = #{ceo_name}, bank_name = #{bank_name}, account_no = #{account_no}, note = #{note},
WHERE company_no = #{company_no}
